import React, { useContext, useState } from 'react';
import domtoimage from 'dom-to-image';
import ConfProject from '../../../contexts/ConfProject';

import './style.scss'

function ButtonConverTo() {

  const { codeFieldRef } = useContext(ConfProject);
  const [imagem, setImagem] = useState('')

  function convertToImage(e) {
    domtoimage.toPng(codeFieldRef.current)
      .then((response) => {
        setImagem(response)
      })
  }

  return (
    <button type="button" className="convert" onClick={convertToImage} download={imagem ? imagem : ''}>Converter para PNG!</button>
  )
}

export default ButtonConverTo
