import React, { useContext } from 'react';
import domtoimage from 'dom-to-image';
import ConfProject from '../../../contexts/ConfProject';

import './style.scss'
import alertDownload from '../AlertDownload';

function ButtonConverTo() {

  const { codeFieldRef } = useContext(ConfProject);

  function convertToImage(e) {
    domtoimage.toBlob(codeFieldRef.current)
      .then((response) => {
        window.saveAs(response, 'meuprojeto.png');
      })
  }

  return (
    <>
      <alertDownload />
      <button type="button" className="convert" onClick={convertToImage}>Converter para PNG!</button>
    </>
  )
}

export default ButtonConverTo
