import React, { useState } from 'react';

const ConfProject = React.createContext();
const codeFieldRef = React.createRef(null);
const menuRef = React.createRef(null);

export function ConfProjectContext(props) {

  const [codes, setCodes] = useState([]);

  function setDados(dados) {
    const newDados = [...codes, dados]
    setCodes(newDados)
  }

  return (
    <ConfProject.Provider value={{ codeFieldRef, menuRef, setDados, codes }}>
      {props.children}
    </ConfProject.Provider>
  )
}

export default ConfProject;
