import React, { useContext, useEffect } from 'react';
import EditorDeCodigo from './EditorDeCodigo';
import CodigoInfos from './CodigoInfos';
import ConfProject from '../../contexts/ConfProject';

function EditCode() {
  const { codeTabRef, comunityTabRef } = useContext(ConfProject);

  codeTabRef.current.classList.remove('menu__tab-active')
  comunityTabRef.current.classList.add('menu__tab-active')

  return (
    <>
      <EditorDeCodigo />
      <CodigoInfos />
    </>
  );
}

export default EditCode
