import React, { useContext } from 'react';
import ConfProject from '../../../contexts/ConfProject';
import CodeField from './CodeField';
import hljs from 'highlight.js';

import './style.scss'
import './highlight.css'
import CodeEditorContainer from '../subcomponents/CodeEditorContainer';
import CodeEditor from '../subcomponents/CodeEditor';
import HighlightButton from '../subcomponents/HighLightButton';

function EditorDeCodigo() {

  const { codeFieldRef } = useContext(ConfProject)

  function handleHighlight() {
    const CodePlace = codeFieldRef.current.children[0].lastChild
    hljs.highlightBlock(CodePlace)
  }

  return (
    <CodeEditorContainer>
      <CodeEditor ref={codeFieldRef}>
        <pre>
          <CodeField editable={true} />
        </pre>
      </CodeEditor>
      <HighlightButton onClick={handleHighlight}>
        <p className="viewHighLight__text">Visualizar com o highlight</p>
      </HighlightButton>
    </CodeEditorContainer>
  );
}

export default EditorDeCodigo;
