import React, { useContext, useEffect, useRef } from 'react';
import ConfProject from '../../../contexts/ConfProject';

import './style.scss'

function CodigoInfos() {

  const { codeFieldRef, saveProject } = useContext(ConfProject);
  const nameProjectRef = React.createRef(null);
  const descriptionProjectRef = React.createRef(null);
  const languageProjectRef = React.createRef(null);
  const colorProjectRef = React.createRef(null);

  function handleHighLightColor(e) {
    console.log(e)
    codeFieldRef.current.style.background = e.target.value
  }

  return (
    <form className="infos" onSubmit={(event) => event.preventDefault()}
    >
      <div className="seuProjeto">
        <h2 className="seuProjeto__title">Seu projeto</h2>
        <input className="seuProjeto__name" type="text" placeholder="Nome do seu projeto" ref={nameProjectRef} />
        <textarea className="seuProjeto__description" type="text" placeholder="Descrição do seu projeto" ref={descriptionProjectRef} />
      </div>
      <div className="personalizacao">
        <h2 className="personalizacao__title">Personalização</h2>
        <select className="personalizacao__language" name="" id="" ref={languageProjectRef}>
          <option value="javascript">JavaScript</option>
          <option value="typescript">TypeScript</option>
        </select>
        <input className="personalizacao__color" onChange={handleHighLightColor} data-testid="color" type="color" name="" id="" ref={colorProjectRef} />
        <button type="submit" className="personalizacao__button" onClick={() => saveProject({
          nome: nameProjectRef.current.value,
          descricao: descriptionProjectRef.current.value,
          language: languageProjectRef.current.value,
          color: colorProjectRef.current.value,
          project: codeFieldRef.current.lastChild.lastChild.outerText,
        })}>Salvar Projeto</button>
      </div>
    </form>
  );
}

export default CodigoInfos
