import React, { useContext } from 'react';

import { FaUsers } from "react-icons/fa";
import { FaCode } from "react-icons/fa";
import ConfProject from '../../contexts/ConfProject';

function Tabs() {
  const ConfProjectContext = useContext(ConfProject)
  const codeTabRef = React.createRef(null);
  const comunityTabRef = React.createRef(null);

  function handleToggleContent(content, ref) {
    const reference = ref.current
    const references = [codeTabRef, comunityTabRef];
    const referenceFiltered = references.filter((r) => {
      return r !== ref
    })
    referenceFiltered[0].current.classList.remove('menu__tab-active')

    if (!reference.classList.contains('menu__tab-active')) {
      reference.classList.toggle('menu__tab-active')
    }

    ConfProjectContext.setContent(content)
  }

  return (
    <>
      <div className="menu__tab codeTab menu__tab-active" ref={codeTabRef} onClick={() => handleToggleContent(0, codeTabRef)}>
        <div className="menu__tab__image"><FaCode /></div>
        <p className="menu__tab__name">Editor de c√≥digo</p>
      </div>
      <div className="menu__tab comunityTab" ref={comunityTabRef} onClick={() => handleToggleContent(1, comunityTabRef)}>
        <div className="menu__tab__image"><FaUsers /></div>
        <p className="menu__tab__name">Comunidade</p>
      </div>
    </>
  )
}

export default Tabs
