import React, { useContext } from 'react';
import ConfProject from '../../../contexts/ConfProject';
import CodeField from './CodeField';
import hljs from 'highlight.js';

import './style.scss'
import './highlight.css'

function EditorDeCodigo() {

  const ConfProjectContext = useContext(ConfProject)

  console.log(ConfProjectContext)

  function handleHighlight() {
    const CodePlace = ConfProjectContext.codeFieldRef.current.children[0].lastChild
    hljs.highlightBlock(CodePlace)
  }

  return (
    <section className="codeEditorContainer">
      <div className="codeEditor" ref={ConfProjectContext.codeFieldRef} data-testid="editor">
        <pre>
          <CodeField editable={true} />
        </pre>
      </div>
      <div className="viewHighLight" onClick={handleHighlight}><p className="viewHighLight__text">Visualizar com o highlight</p></div>
    </section>
  );
}

export default EditorDeCodigo;
