import React, { useState } from 'react';

const ConfProject = React.createContext();
const codeFieldRef = React.createRef(null);
const MenuRef = React.createRef(null);

export function ConfProjectContext(props) {
  const [content, setContent] = useState(0);
  const [projects, setProjects] = useState([]);

  function saveProject(projectConf) {
    setProjects([...projects, projectConf])
    console.log(projects)
  }

  return (
    <ConfProject.Provider value={{
      codeFieldRef,
      content,
      setContent,
      MenuRef,
      saveProject,
      projects,
    }}>
      {props.children}
    </ConfProject.Provider>
  )
}

export default ConfProject;
