import React, { useContext } from 'react';
import ConfProject from '../../../contexts/ConfProject';
import CodeField from './CodeField';
import hljs from 'highlight.js';

import './style.scss'
import './highlight.css'

function EditorDeCodigo() {

  const { codeFieldRef } = useContext(ConfProject)

  function handleHighlight() {
    const codePlace = codeFieldRef.current.children[0].lastChild
    console.log(codeFieldRef)
    console.log(codePlace)
    hljs.highlightElement(codePlace)
  }

  return (
    <section className="codeEditorContainer">
      <div className="codeEditor" ref={codeFieldRef}>
        <CodeField />
      </div>
      <div className="viewHighLight" onClick={handleHighlight}><p className="viewHighLight__text">Visualizar com o highlight</p></div>
    </section>
  );
}

export default EditorDeCodigo;
