import React, { useContext, useEffect } from 'react';
import Header from './components/Header'
import MenuNavegacao from './components/MenuNavegacao';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import EditCode from './components/EditCode';
import Comunity from './components/Comunity';
import Error404 from './components/404';

import './styles/reset.css';
import './styles/global.scss';
import './styles/app.scss';
import ConfProject from './contexts/ConfProject';

function App() {

  const { setCodes } = useContext(ConfProject);

  useEffect(() => {
    localStorage['Dados'] ? setCodes(JSON.parse(localStorage['Dados'])) : setCodes([])
  })

  return (
    <main className="challenge">
      <Header />
      <section className="body">
        <Router>
          <MenuNavegacao />
          <Switch>
            <Route exact path="/">
              <EditCode />
            </Route>
            <Route path="/comunidade">
              <Comunity />
            </Route>
            <Route>
              <Error404 />
            </Route>
          </Switch>
        </Router>
      </section>
    </main>
  );
}

export default App;
