import React, { useContext, useEffect } from 'react';
import ConfProject from '../../../contexts/ConfProject';

function CodeField({ color, project }) {
  const { codeFieldRef } = useContext(ConfProject);

  useEffect(() => {
    console.log(codeFieldRef)
    console.log(color)
    codeFieldRef.current.style.background = color
  })

  return (
    <div className="codeEditor__highlight">
      <div className="codeEditor__highlight__circle">
        <span className="codeEditor__highlight__circle-1"></span>
        <span className="codeEditor__highlight__circle-2"></span>
        <span className="codeEditor__highlight__circle-3"></span>
      </div>
      <code contentEditable className="codeEditor__highlight__codeField language-javascript" type="text" ref={codeFieldRef}>{project}</code>
    </div>
  );
}

export default CodeField
