import React from 'react';

import { FaUsers } from "react-icons/fa";
import { FaCode } from "react-icons/fa";
import { Link } from 'react-router-dom';

function Tabs() {
  const codeTabRef = React.createRef(null);
  const comunityTabRef = React.createRef(null);

  function menuToggle(e) {
    const href = e.currentTarget.getAtrribute('href')
    console.log(href)

    if (href === '/') {
      comunityTabRef.current.classList.remove('menu__tab-active')
      codeTabRef.current.classList.add('menu__tab-active')
    } else if (href === '/comunidade') {
      codeTabRef.current.classList.remove('menu__tab-active')
      comunityTabRef.current.classList.add('menu__tab-active')
    }
  }

  return (
    <>
      <Link to="/" className="menu__tab codeTab" ref={codeTabRef} onClick={menuToggle}>
        <div className="menu__tab__image"><FaCode /></div>
        <p className="menu__tab__name">Editor de c√≥digo</p>
      </Link>
      <Link to="/comunidade" className="menu__tab comunityTab" ref={comunityTabRef} onClick={menuToggle}>
        <div className="menu__tab__image"><FaUsers /></div>
        <p className="menu__tab__name">Comunidade</p>
      </Link>
    </>
  )
}

export default Tabs
