import React, { useContext } from 'react';
import ConfProject from '../../../contexts/ConfProject';

function CodeField() {
  const { codeFieldRef } = useContext(ConfProject);

  return (
    <div className="codeEditor__highlight">
      <div className="codeEditor__highlight__circle" ref={codeFieldRef}>
        <span className="codeEditor__highlight__circle-1"></span>
        <span className="codeEditor__highlight__circle-2"></span>
        <span className="codeEditor__highlight__circle-3"></span>
      </div>
      <textarea className="codeEditor__highlight__codeField" type="text" />
    </div>
  );
}

export default CodeField
